mapper.update:
UPDATE
	ADMIN
SET
	ADMIN_PW=#{admin_PW}
	,ADMIN_NAME=#{admin_Name}
	,ADMIN_PHONE=#{admin_Phone}
	,ADMIN_EMAIL=#{admin_Email}
	,ADMIN_DEPARTMENT=#{admin_Department}
	,ADMIN_TITLE=#{admin_Title}
WHERE
	ADMIN_ID=#{admin_ID}

mapper.insert:
INSERT INTO
	ADMINTODO (
		ADMINTODO_SEQ
		,ADMIN_ID
		,ADMINTODO_CONTENT
		,ADMINTODO_INDATE
	) VALUES (
		ADMINTODO_SEQ.NEXTVAL
		,#{admin_ID}
		,#{adminTodo_Content}
		,SYSDATE
	)

mapper.update:
UPDATE
	ADMINTODO
SET
	ADMINTODO_CONTENT = #{adminTodo_Content}
WHERE
	ADMIN_ID = #{admin_ID}
AND
	ADMINTODO_SEQ = #{adminTodo_Seq}

mapper.insert:
INSERT INTO
	ADMINMESSAGE (
		ADMINMESSAGE_SEQ
		,ADMIN_NAME
		,ADMIN_ID
		,ADMINMESSAGE_CONTENT
		,ADMINMESSAGE_INDATE
	) VALUES (
		ADMINMESSAGE_SEQ.NEXTVAL
		,#{admin_Name}
		,#{admin_ID}
		,#{adminMessage_Content}
		,SYSDATE
	)
